  display: flex;
}

.bg-primary {
  color: white;
  background-color: hsl(12deg, 88%, 59%);
}

.bg-secondary {
  color: hsl(12deg, 88%, 59%);
  background-color: white;
}

.bg-footer {
  color: white;
  background-color: hsl(233deg, 12%, 13%);
}

.container {
  max-width: 1300px;
  padding: 2rem;
  margin-left: auto;
  margin-right: auto;
}

.btn {
  text-transform: capitalize;
  padding: 1rem 2rem;
  border-radius: 100px;
  box-shadow: 0 8px 8px -3px hsla(12deg, 88%, 59%, 0.41);
}
.btn.bg-primary:hover {
  opacity: 0.9;
}

