            text-align: center;
            font-weight: 700;
            display: grid;
            width: 80%;
            padding: 2rem;
            margin: 0 10%;
            border-radius: 5px;
            box-shadow: 0px 0px 13px 1px rgb(0 0 0 / 10%);
            background-color: white;
        }
    }

    nav.show {
        display: block;
    }

    .menu {
        z-index: 4;
        height: 22px;
    }

    @media (min-width: $tablet-size) {
        .menu {
            display: none;
        }

        nav, .btn {
            display: block;
        }

        nav{
            position: static;
            
            ul {
                position: static;
                display: flex;
                font-weight: 400;
                width: fit-content;
                padding: 0;
                margin: 0;
                box-shadow: none;
            }

            .back-drop {
                display: none;
            }
        }
    }
}

