}

sources.styles\\_about-section.scss:
@use "variables" as *;
@use "mixin" as *;

.about {
    text-align: center;
    padding: 2rem 1rem !important;
    padding-right: 0 !important;
    margin-top: 2rem;

    h2 {
        font-size: 1.9rem;
        padding-right: 1rem;
        @include max-width();
    }
    
    p {
        padding-right: 1rem;
        @include max-width();
    }

    .features {
        margin: 3rem 0 0;
        gap: 2rem;
    }
    
    .feature {
        text-align: left;

        .header {   
            display: flex;
            align-items: center;
            gap: .6rem;
            font-weight: 700;
            border-top-left-radius: 100vh;
            border-bottom-left-radius: 100vh;
            background-color: $clr-accent;

            .index {
                padding: .8rem 1.7rem;
                border-radius: 100vh;
            }

            h3 {
                font-size: 1.1rem;
                margin: 0
            }
        }

        p {
            @include reset-margin();
        }
    }

    
    @media (min-width: $tablet-size) {
        text-align: left;
        gap: 1rem;

        h2 {
            font-size: 2.4rem;
            max-width: 500px;
            padding: 0;
            margin-top: 0;
            @include reset-margin();
        }

        p {
            max-width: 350px;
            padding: 0;
            @include reset-margin();
        }

        .features {
            margin: 0;
            justify-items: end;
        }

        .feature {
            .header {
                gap: 1.5rem;
                background-color: transparent;

                .index {
                    padding: .6rem 1.7rem;
                }
            }


            p {
                max-width: 450px;
                margin-left: 6.2rem;
            }
        }
    }
}

sources.styles\\_testimonial-section.scss:
@use "variables" as *;

.testimonials {
    text-align: center;
    padding: 0 !important;
    margin-bottom: 3.5rem;

    h2 {
        font-size: 2.4rem;
    }

    .scrollable {
        --scroll: 0;

        width: 100%;
        padding: 2rem 1rem 0;
        margin: 4rem auto 0;
        overflow: hidden;
        
        &::-webkit-scrollbar {
            display: none;
        }

        .testimonial {
            flex: 0 0 100%;
            padding-bottom: 1rem;
            background-color: $clr-secondary;
            transform: translateX(var(--scroll));
            transition: transform .35s ease-in;

            .avatar {
                width: 60px;
                transform: translateY(-50%);
            }
            
            .name {
                font-weight: 700;
            }
        }
        
        @media (min-width: $tablet-size) {
            gap: 2rem;
            overflow-x: auto;
            margin-bottom: 4rem;

            .testimonial {
                max-width: 520px;
            }
        }

        @media (min-width: 2200px) {
            justify-content: center;
        }
    }

    .slider-dots {
        justify-content: center;
        gap: .4rem;
        margin: 1.5rem 0 4.2rem;

        .dot {
            width: 10px;
            aspect-ratio: 1;
            border-radius: 100vh;
            border: 1px solid $clr-primary;
            cursor: pointer;

            &.active {
                background-color: $clr-primary;
            }
        }

        @media (min-width: $tablet-size) {            
            display: none;
        }
    }
}
